import React from 'react';

/**
 * Defines the available color variants for the Badge component.
 * Maps to specific Tailwind CSS background and text colors.
 */
type BadgeVariant = 'error' | 'success' | 'warning' | 'info' | 'default';

interface BadgeProps {
  /**
   * The text content displayed within the badge.
   */
  text: string;
  /**
   * Defines the color scheme of the badge.
   * @default 'default'
   */
  variant?: BadgeVariant;
}

/**
 * A small, rounded label used to highlight important information or statuses.
 */
const Badge: React.FC<BadgeProps> = ({ text, variant = 'default' }) => {
  // Mapping variants to specific Tailwind CSS classes
  const variantClasses: Record<BadgeVariant, string> = {
    error: 'bg-red-500 text-white',      // #ef4444, #ffffff
    success: 'bg-green-500 text-white',
    warning: 'bg-yellow-500 text-gray-800',
    info: 'bg-blue-500 text-white',
    default: 'bg-gray-200 text-gray-800',
  };

  const baseClasses =
    'inline-flex items-center justify-center ' +
    'px-2 py-1 ' + // padding: 4px 8px (px-2 is 8px, py-1 is 4px in default config)
    'text-xs font-semibold ' + // fontSize: 12px, fontWeight: 600
    'rounded-full ' + // borderRadius: 9999px
    'uppercase tracking-wider';

  const combinedClasses = `${baseClasses} ${variantClasses[variant]}`;

  return (
    <span
      className={combinedClasses}
      role="status" // Indicates this element provides status information
      aria-label={`${variant} status: ${text}`}
    >
      {text}
    </span>
  );
};

export default Badge;

