  const variantClasses = {
    primary:
      'bg-violet-600 text-white hover:bg-violet-700 focus:ring-violet-300 font-medium', // Matches spec
    secondary:
      'bg-gray-200 text-gray-800 hover:bg-gray-300 focus:ring-gray-100 font-medium',
    danger:
      'bg-red-600 text-white hover:bg-red-700 focus:ring-red-300 font-medium',
    ghost: 'bg-transparent text-violet-600 hover:bg-violet-50 focus:ring-violet-100 font-medium',
  };

  // Combine all classes
  const classes = [
    baseClasses,
    sizeClasses[size],
    paddingClasses[size],
