/**
 * A versatile and customizable button component matching the Figma design specifications.
 */
const Button: React.FC<ButtonProps> = ({
  text,
  variant = 'primary',
  size = 'medium',
  disabled = false,
  className = '',
  onClick,
  ...rest
}) => {
  // 1. Define base styling
  const baseStyles =
    'inline-flex items-center justify-center font-medium transition-colors duration-200 ease-in-out focus:outline-none focus:ring-2 focus:ring-offset-2 rounded-md';

  // 2. Define size-specific styling
  const sizeStyles = React.useMemo(() => {
    switch (size) {
      case 'small':
        return 'px-3 py-1.5 text-xs'; // Example: 12px font, smaller padding
      case 'large':
        return 'px-6 py-3 text-base'; // Example: 16px font, larger padding
      case 'medium':
      default:
        // Matches the Figma specification: padding: 8px 16px, fontSize: 14px, fontWeight: 500
        return 'px-4 py-2 text-sm';
    }
  }, [size]);

  // 3. Define variant-specific styling
  const variantStyles = React.useMemo(() => {
    switch (variant) {
      case 'secondary':
        return 'bg-gray-200 text-gray-800 hover:bg-gray-300 focus:ring-gray-400';
      case 'ghost':
        return 'bg-transparent text-indigo-600 hover:bg-indigo-50 focus:ring-indigo-300';
