      default:
        // Matches the Figma specification: backgroundColor: #7c3aed, color: #ffffff
        return 'bg-violet-600 text-white hover:bg-violet-700 focus:ring-violet-500';
    }
  }, [variant]);

  // 4. Define disabled styling
  const disabledStyles = disabled
    ? 'opacity-50 cursor-not-allowed shadow-none'
    : 'shadow-sm';

  // Combine all styles
  const buttonClasses = `${baseStyles} ${sizeStyles} ${variantStyles} ${disabledStyles} ${className}`;

  return (
    <button
      type="button" // Default to button type
      className={buttonClasses}
      disabled={disabled}
      onClick={onClick}
      aria-label={text}
      {...rest}
    >
      {text}
    </button>
