import React from 'react';

/**
 * Props for the Dialog component.
 */
interface DialogProps {
  /** The title displayed at the top of the dialog. */
  title: string;
  /** The main descriptive text content of the dialog. */
  description: string;
  /** Text for the primary action button (e.g., "Confirm", "Save"). */
  primaryAction: string;
  /** Text for the secondary action button (e.g., "Cancel", "Dismiss"). */
  secondaryAction: string;
  /** Optional handler for the primary action button click. */
  onPrimaryAction?: () => void;
  /** Optional handler for the secondary action button click/close action. */
  onSecondaryAction?: () => void;
  /** Determines if the close (X) button is shown in the top right corner. Defaults to true. */
  showCloseButton?: boolean;
}

const Dialog: React.FC<DialogProps> = ({
  title,
  description,
  primaryAction,
  secondaryAction,
  onPrimaryAction,
  onSecondaryAction,
  showCloseButton = true, // Default based on component data
}) => {
  // Simple SVG for the close button (X icon)
  const CloseIcon = () => (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      className="h-5 w-5"
      viewBox="0 0 20 20"
      fill="currentColor"
    >
      <path
        fillRule="evenodd"
        d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z"
        clipRule="evenodd"
      />
    </svg>
  );

  return (
    // Backdrop/Overlay (Assuming the parent handles the actual modal wrapper,
    // this component focuses on the dialog box itself)
    <div
      className="bg-white rounded-[12px] shadow-xl w-[400px] min-h-[200px] p-6 flex flex-col"
      role="dialog"
      aria-modal="true"
      aria-labelledby="dialog-title"
      aria-describedby="dialog-description"
      // Note: The specific shadow is 'shadow-xl' in Tailwind, which is
      // '0 20px 25px -5px rgba(0,0,0,0.1), 0 10px 10px -5px rgba(0,0,0,0.04)'
    >
      {/* Header */}
      <div className="flex justify-between items-start mb-4">
        <h2 id="dialog-title" className="text-xl font-semibold text-gray-900">
          {title}
        </h2>
        {showCloseButton && (
          <button
            onClick={onSecondaryAction}
            className="p-1 rounded-full text-gray-400 hover:text-gray-500 hover:bg-gray-50 transition-colors focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            aria-label="Close dialog"
          >
            <CloseIcon />
          </button>
        )}
      </div>

      {/* Content */}
      <div className="flex-grow mb-6">
        <p id="dialog-description" className="text-sm text-gray-500">
          {description}
        </p>
      </div>

      {/* Actions */}
      <div className="flex justify-end space-x-3">
        {/* Secondary Action (Cancel) */}
        <button
          type="button"
          onClick={onSecondaryAction}
          className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg shadow-sm hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-colors"
        >
          {secondaryAction}
        </button>

        {/* Primary Action (Confirm) */}
        <button
          type="button"
          onClick={onPrimaryAction}
          className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 border border-transparent rounded-lg shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-colors"
        >
          {primaryAction}
        </button>
      </div>
    </div>
  );
};

export default Dialog;

