import React from 'react';

/**
 * TypeScript interface for the Input component props.
 * The component represents a standard text input field.
 */
interface InputProps {
  /** The type of the input element (e.g., 'text', 'email', 'password'). */
  type?: 'text' | 'email' | 'password' | 'number';
  /** Placeholder text displayed when the input is empty. */
  placeholder?: string;
  /** Optional label text displayed above the input field (used for accessibility). */
  label?: string;
  /** The current value of the input field (controlled component). */
  value?: string;
  /** Function called when the input value changes. */
  onChange?: (event: React.ChangeEvent<HTMLInputElement>) => void;
  /** Unique identifier for the input element. */
  id?: string;
  /** If true, the input field will be disabled. */
  disabled?: boolean;
}

/**
 * A reusable input component styled according to the specified Figma design.
 *
 * Design Specifications:
 * - Background Color: #ffffff (bg-white)
 * - Border: 1px solid #d1d5db (border border-gray-300) -> Corrected based on previous requirement #7 to #CCCCCC (border-gray-400 equivalent)
 * - Border Radius: 6px (rounded-[6px])
 * - Padding: 8px 12px (p-2 px-3) -> Corrected based on previous requirement #9 to 10px (p-[10px])
 * - Font Size: 14px (text-sm) -> Corrected based on previous requirement #8 to 16px (text-base)
 * - Width: 240px (w-[240px])
 * - Height: 40px (h-[40px])
 * - Placeholder: "Enter text..."
 * - Label: Removed (Requirement #10)
 */
const Input: React.FC<InputProps> = ({
  type = 'text',
  placeholder = 'Enter text...', // Requirement #6: Placeholder text is 'Enter text...'
  label = 'Input Field', // Included for accessibility, but not rendered
  value,
  onChange,
  id = 'standard-input',
  disabled = false,
}) => {
  // Requirement #7: Change the border color to #CCCCCC (Tailwind equivalent: gray-400 or custom hex)
  // Using custom hex for precise matching: border-[#CCCCCC]
  const customBorderColor = 'border-[#CCCCCC]';

  // Requirement #8: Increase the font size to 16px (text-base)
  // Requirement #9: Adjust the padding to 10px (p-[10px])

  const inputBaseClasses = `
    w-[240px] h-10
    bg-white
    border ${customBorderColor}
    rounded-[6px]
    text-base          /* 16px font size */
    text-gray-900
    p-[10px]           /* 10px padding */
    focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500
    placeholder:text-gray-400
    transition duration-150 ease-in-out
  `;

  // Disabled state classes
  const disabledClasses = disabled
    ? 'opacity-60 cursor-not-allowed bg-gray-50'
    : '';

  return (
    <div className="flex flex-col">
      {/* 
        Requirement #10: Remove the label 'Input Field' above the input box.
        The label prop is still used for aria-label, but not rendered visually.
      */}

      {/* Input Field */}
      <input
        id={id}
        type={type}
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        disabled={disabled}
        // Use the label or placeholder for accessibility
        aria-label={label || placeholder || 'Text input field'}
        aria-disabled={disabled}
        className={`${inputBaseClasses} ${disabledClasses}`}
      />
    </div>
  );
};

export default Input;

