/**
 * Defines the available sizes for the Button component.
 */
type ButtonSize = 'small' | 'medium' | 'large';

/**
 * Interface for the Button component props.
 */
interface ButtonProps {
  /**
   * The text content displayed inside the button.
   */
  text: string;
  /**
   * The visual style variant of the button.
   * @default 'primary'
   */
  variant?: ButtonVariant;
  /**
   * The size of the button.
   * @default 'medium'
   */
  size?: ButtonSize;
  /**
   * Optional click handler for the button.
   */
  onClick?: (event: React.MouseEvent<HTMLButtonElement>) => void;
  /**
   * Controls whether the button is disabled.
   * @default false
   */
  disabled?: boolean;
  /**
   * Optional aria-label for accessibility.
   */
  ariaLabel?: string;
  /**
   * Optional HTML type attribute for the button.
   * @default 'button'
   */
  type?: 'button' | 'submit' | 'reset';
}

/**
 * A reusable button component styled using Tailwind CSS, matching the provided specifications.
 *
 * Specifications from JSON:
 * - backgroundColor: #7c3aed (violet-600)
 * - color: #ffffff (white)
 * - padding: 8px 16px (py-2 px-4)
 * - borderRadius: 6px (rounded-md)
 * - fontSize: 14px (text-sm)
 * - fontWeight: 500 (font-medium)
 * - text: "Click Me"
 */
const Button: React.FC<ButtonProps> = ({
  text,
  variant = 'primary',
  size = 'medium',
  onClick,
  disabled = false,
  ariaLabel,
  type = 'button',
}) => {
  // --- Tailwind Class Mapping ---

  // 1. Base styles (shared across all variants/sizes)
  const baseClasses =
    'inline-flex items-center justify-center transition-colors duration-150 focus:outline-none focus:ring-4 disabled:opacity-50 disabled:cursor-not-allowed';

  // 2. Size styles
  const sizeClasses = {
    small: 'text-xs rounded',
    medium: 'text-sm rounded-md', // Matches 14px font size and 6px border radius from spec
    large: 'text-base rounded-lg',
  };

  // 3. Padding styles (based on spec: 8px 16px -> py-2 px-4)
  const paddingClasses = {
    small: 'py-1.5 px-3',
    medium: 'py-2 px-4', // Matches 8px 16px padding from spec
    large: 'py-3 px-6',
  };

